mapper.select:
SELECT EVENT_POSTNUM, MEMBER_ID, MEMBER_NAME, EVENT_TITLE, EVENT_CONTENT, EVENT_VIEWS, EVENT_REGDATE, MEMBER_LEVEL
FROM EVENTBOARD
WHERE EVENT_POSTNUM = #{event_postnum }

SELECT * FROM ( SELECT ROWNUM AS RNUM, N.* FROM EVENTBOARD N WHERE 1=1
AND ( (upper(EVENT_TITLE) like '%' || upper(#{txt_search}) || '%') or (upper(EVENT_CONTENT) like '%' || upper(#{txt_search}) || '%') )
ORDER BY EVENT_POSTNUM DESC
) WHERE RNUM BETWEEN #{firstIndex} +1 AND #{firstIndex} + #{recordCountPerPage}
